手写冒泡排序
```C++
#include<iostream>
#include<string>
#include<vector>
using namespace std;

//升序
void bubbleSort(int *A, vector<string> &name, int n){
    int tmp;
    string stmp;
    for(int i=0;i<n-1;i++){//已经排序好的个数
        bool flag = false;
        for(int j=n-1;j>i;j--){//从最后向前找最小的
            if(A[j]<A[j-1]){
                flag = true;
                tmp = A[j-1];
                A[j-1]=A[j];
                A[j]=tmp;
                stmp = name[j-1];
                name[j-1]=name[j];
                name[j]=stmp;
            }
        }
        if(!flag) break;
    }
}

//降序
void bubbleSort2(int *A, vector<string> &name, int n){
    int tmp;
    string stmp;
    for(int i=0;i<n-1;i++){//已经排序好的个数
        bool flag = false;
        for(int j=n-1;j>i;j--){//从最后向前找最小的
            if(A[j]>A[j-1]){
                flag = true;
                tmp = A[j-1];
                A[j-1]=A[j];
                A[j]=tmp;
                stmp = name[j-1];
                name[j-1]=name[j];
                name[j]=stmp;
            }
        }
        if(!flag) break;
    }
}

int main(){
    vector<string> names(1000);
    int records[1000];
    int n, tag;
    while(cin>>n>>tag){
        for(int i=0;i<n;i++){
            cin>>names[i]>>records[i];
        }

        if(tag)
            bubbleSort(records, names, n);
        else
            bubbleSort2(records, names, n);

        for(int i=0;i<n;i++) 
            cout<<names[i]<<" "<<records[i]<<endl;
    }
}
```

